mapper.select:
SELECT DS.daily_id ,DS.note_id ,DS.mood_id ,DS.`date` ,NS.note_title ,NS.note_content ,MS.mood_level ,M.`description` FROM ( SELECT daily_id ,note_id ,mood_id ,DATE_FORMAT(`date`, '%Y-%m-%d') as `date` FROM dailies WHERE user_id = #{userId}
AND `date` >= #{startDate} AND `date` <= #{endDate}
) AS DS LEFT JOIN notes AS NS ON DS.note_id = NS.note_id LEFT JOIN moods AS MS ON DS.mood_id = MS.mood_id LEFT JOIN mood AS M ON MS.mood_level = M.`level` ORDER BY `date` ASC
