mapper.select:
SELECT DS.daily_id ,DS.note_id ,DS.mood_id ,DS.`date` ,NS.note_title ,NS.note_content ,MS.mood_level ,M.`description` FROM ( SELECT daily_id ,note_id ,mood_id ,DATE_FORMAT(`date`, '%Y-%m-%d') as `date` FROM dailies WHERE user_id = #{userId}
AND `date` >= #{startDate} AND `date` <= #{endDate}
ORDER BY `date` DESC LIMIT #{limit} offset #{offset} ) AS DS LEFT JOIN notes AS NS ON DS.note_id = NS.note_id LEFT JOIN moods AS MS ON DS.mood_id = MS.mood_id LEFT JOIN mood AS M ON MS.mood_level = M.`level` WHERE 1=1
AND NS.note_title LIKE '%${title}%'
AND NS.note_content LIKE '%${content}%'

SELECT COUNT(daily_id)
DS.`date` MS.mood_level DS.`date`
FROM ( SELECT daily_id ,note_id ,mood_id ,DATE_FORMAT(`date`, '%Y-%m-%d') as `date` FROM dailies WHERE user_id = #{userId}
AND `date` >= #{startDate} AND `date` <= #{endDate}
) AS DS LEFT JOIN notes AS NS ON DS.note_id = NS.note_id LEFT JOIN moods AS MS ON DS.mood_id = MS.mood_id LEFT JOIN mood AS M ON MS.mood_level = M.`level` GROUP BY
DS.`date` MS.mood_level DS.`date`

SELECT daily_id
	, note_id
    , mood_id
FROM dailies
WHERE user_id = #{userId}
AND `date` = #{date}

mapper.delete:
DELETE FROM dailies (
WHERE daily_id = #{dailyId}
AND user_id = #{userId}
