    , 'Y'
    , 'Y'
), (

	#{guestProfileId}
    , #{hostProfileId}
    , 'N'
    , 'N'
    , 'N'
    , 'N'
)

mapper.select:
SELECT guest_profile_id
FROM neighbor
WHERE host_profile_id = (
	SELECT user_profile_id
	FROM user_profile
	WHERE user_id = #{userId}
)
AND neighbor_id = #{neighborId}

mapper.update:
UPDATE neighbor
SET synchronize = #{synchronize}
	,updated_at = NOW()
WHERE host_profile_id = (
	SELECT user_profile_id
	FROM user_profile
	WHERE user_id = #{userId}
)
AND guest_profile_id = #{guestProfileId}

UPDATE neighbor
SET synchronize = #{synchronize}
	,updated_at = NOW()
WHERE guest_profile_id = (
	SELECT user_profile_id
	FROM user_profile
	WHERE user_id = #{userId}
)
AND host_profile_id = #{guestProfileId}

UPDATE neighbor

SET 

			accept_chat = #{acceptChat}
			, external_access = #{externalAccess} 


			accept_chat = #{acceptChat}


			external_access = #{externalAccess}



	, updated_at = NOW()
WHERE host_profile_id = (
	SELECT user_profile_id
	FROM user_profile
	WHERE user_id = #{userId}
)

mapper.delete:
DELETE FROM neighbor
WHERE host_profile_id = (
	SELECT user_profile_id
	FROM user_profile
	WHERE user_id = #{userId}
)
AND guest_profile_id = #{guestProfileId}

DELETE FROM neighbor
WHERE guest_profile_id = (
	SELECT user_profile_id
	FROM user_profile
	WHERE user_id = #{userId}
)
