mapper.insert:

	SELECT user_profile_id
	FROM user_profile
	WHERE user_id = #{userId}

INSERT INTO neighbor (
	host_profile_id
    , guest_profile_id
    , requester
    , synchronize
    , chatroom_active
    , cal_ext_access
) VALUES (

	#{hostProfileId}
    , #{guestProfileId}
    , 'Y'
    , 'N'
    , 'N'
    , 'Y'
), (

	#{guestProfileId}
    , #{hostProfileId}
    , 'N'
    , 'N'
    , 'N'
    , 'N'
)

mapper.select:
SELECT guest_profile_id
FROM neighbor
WHERE host_profile_id = (
	SELECT user_profile_id
	FROM user_profile
	WHERE user_id = #{userId}
)
AND neighbor_id = #{neighborId}

mapper.update:
UPDATE neighbor
SET synchronize = #{synchronize}
	,updated_at = NOW()
WHERE host_profile_id = (
	SELECT user_profile_id
	FROM user_profile
	WHERE user_id = #{userId}
)
AND guest_profile_id = #{guestProfileId}

UPDATE neighbor
SET synchronize = #{synchronize}
	,updated_at = NOW()
WHERE guest_profile_id = (
	SELECT user_profile_id
	FROM user_profile
	WHERE user_id = #{userId}
)
AND host_profile_id = #{guestProfileId}

UPDATE neighbor

SET 

			chatroom_active = #{chatroomActive}
			, cal_ext_access = #{calExtAccess} 


			chatroom_active = #{chatroomActive}


			cal_ext_access = #{calExtAccess}



	, updated_at = NOW()
WHERE host_profile_id = (
	SELECT user_profile_id
	FROM user_profile
	WHERE user_id = #{userId}
)

mapper.delete:
DELETE FROM neighbor
WHERE host_profile_id = (
	SELECT user_profile_id
	FROM user_profile
	WHERE user_id = #{userId}
)
AND guest_profile_id = #{guestProfileId}

DELETE FROM neighbor
WHERE guest_profile_id = (
	SELECT user_profile_id
	FROM user_profile
	WHERE user_id = #{userId}
)
AND host_profile_id = #{guestProfileId}

mapper.update:
UPDATE neighbor
SET chatroom_active = 'Y'
	,updated_at = NOW()
WHERE host_profile_id = (

	SELECT T.id FROM (
		SELECT guest_profile_id AS id
		FROM neighbor
		WHERE neighbor_id = #{neighborId}
	) AS T
)
AND guest_profile_id = (

	SELECT T.id FROM (
		SELECT host_profile_id AS id
		FROM neighbor
		WHERE neighbor_id = #{neighborId}
	) AS T
)

UPDATE neighbor
SET chatroom_active = 'N'
	,updated_at = NOW()
WHERE host_profile_id = (

	SELECT T.id FROM (
		SELECT guest_profile_id AS id
		FROM neighbor
		WHERE neighbor_id = #{neighborId}
	) AS T
)
AND guest_profile_id = (

	SELECT T.id FROM (
		SELECT host_profile_id AS id
		FROM neighbor
		WHERE neighbor_id = #{neighborId}
	) AS T
)
