mapper.insert:
INSERT users (
	username, password_hash, email 
) VALUES ( 
	#{username}, #{password}, #{email} 
)

INSERT user_profile (
	user_id, nickname, image_path, `description`
) VALUES ( 
	#{userId}, #{nickname}, null, "" 
)

mapper.update:
UPDATE user_profile
SET image_path = #{imagePath}
WHERE user_id = #{userId}

mapper.select:
SELECT user_profile_id
FROM user_profile
WHERE nickname = #{nickname}
